mapper.select:
 		SELECT SUM(O_PRICE)
		FROM ORDER_LIST
		WHERE O_NO = #{oNo}

 		SELECT * FROM 
  		(
	  		SELECT ROWNUM RN, R.* 
	  		 FROM(
                SELECT *
		 		FROM APPR
		 		WHERE A_PIC_NAME = #{loginName} AND A_STATEMENT = 'Y'
            ) R
  		)
  		WHERE RN BETWEEN #{page.startRow} AND #{page.endRow}

SELECT O_NO
FROM STATEMENT
WHERE S_NO = #{ano}

SELECT PROJECT_NO
FROM PROJECT
WHERE PROJECT_NO = #{aDocNo}
