mapper.update:
UPDATE CLIENT
SET
C_UMONY = #{sUmoney}
WHERE
C_NO = #{cNo}

UPDATE PRODUCT
SET
P_COUNT = P_COUNT - ${sQnt}
WHERE
P_NO = #{pNo}

mapper.select:
		SELECT * FROM 
  		(
	  		SELECT ROWNUM RN, R.* 
	  		 FROM(
                SELECT *
                FROM SALE S
                ORDER BY SALE_NO DESC
            ) R
  		)
  		WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT C_NAME 
FROM CLIENT 
WHERE C_NO = #{cNo}

SELECT EMPLOYEE_NAME 
FROM EMPLOYEE 
WHERE EMPLOYEE_NO = #{employeeNo}

		SELECT P_NO FROM 
  		(
	  		SELECT ROWNUM RN, R.* 
	  		 FROM(
                SELECT P_NO 
                FROM SALE_LIST 
                WHERE SALE_NO = #{saleNo}
            ) R
  		)
  		WHERE RN BETWEEN 1 AND 1

SELECT P_NAME
FROM PRODUCT
WHERE P_NO = #{pNo}

SELECT COUNT(SALE_LIST_NO)
FROM SALE_LIST
WHERE SALE_NO = #{saleNo}

SELECT WAREHOUSE_NAME 
FROM WAREHOUSE
WHERE WAREHOUSE_NO = #{wareHouseNo}

SELECT PROJECT_NAME
FROM PROJECT
WHERE PROJECT_NO = #{projectNo}

SELECT P_UNIT_PRICE
FROM PRODUCT
WHERE P_NO = #{pNo}
