mapper.select:
  		select * from 
  		(
	  		select rownum rn, c.* 
	  		from(
		  		select * 
		  		from client 
		  		where c_delete_yn = 'N'
		  		order by c_no  		
	  		) c
  		)
  		where rn between #{startRow} and #{endRow}

  		select * from 
  		(
	  		select rownum rn, c.* 
	  		from(
		  		select * 
		  		from client 
		  		where c_delete_yn = 'N'and (
  					c_name like'%${search}%' or c_owner like '%${search}%' or c_addr like '%${search}%'
  				)
		  		order by c_no  		
	  		) c
  		)
  		where rn between #{startRow} and #{endRow}
